import React, { Component } from 'react';
import './App.scss';
import PropTypes from 'prop-types';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import logo from './images/logo-transp.png';
import Typography from '@material-ui/core/Typography';
import { StylesContext } from '@material-ui/styles/StylesProvider';

function TabContainer(props) {
    return (
      <Typography component="div" style={{ padding: 8 * 3 }}>
        {props.children}
      </Typography>
    );
}

TabContainer.propTypes = {
    children: PropTypes.node.isRequired,
};

class Header extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            value: 0
        }
    }

    handleClick = (value) => {
        this.setState({
            value: value
        });
    }


    render() {


        return(
            <div>
                <div className="mainNav">
                    <Tabs value={ this.state.value } indicatorColor="primary"
        textColor="primary" centered >
                        <Tab label="Home" onClick={ this.handleClick } />
                        <Tab label="About" onClick={ this.handleClick } />
                        <Tab label="Work" onClick={ this.handleClick } />
                    </Tabs>
                    <img src={logo} alt="Logo" className="logo"></img>
                </div>
            </div>
        );
    }
}

export default Header;
