import React, { Component } from 'react';
import './App.scss';
import PropTypes from 'prop-types';
import Tabs from '@material-ui/core/Tabs';
import Tab from '@material-ui/core/Tab';
import logo from './images/logo-transp.png';
import { StylesContext } from '@material-ui/styles/StylesProvider';

function TabContainer(props) {
    return (
      <Typography component="div" style={{ padding: 8 * 3 }}>
        {props.children}
      </Typography>
    );
}

TabContainer.propTypes = {
    children: PropTypes.node.isRequired,
  };

class Header extends Component {
    constructor(props) {
        super(props);
        this.handleChange = this.handleChange.bind(this);
        this.state = {
            navItem: 0
        }
    }

    handleChange = (value) => {
        this.setState({
            navItem: value
        });
    }


    render() {


        return(
            <div>
                <div className="mainNav">
                    <Tabs value={ value } onChange={ this.handleChange } indicatorColor="primary"
        textColor="primary" centered >
                        <Tab label="Home" />
                        <Tab label="About" />
                        <Tab label="Work" />
                    </Tabs>
                    <img src={logo} alt="Logo" className="logo"></img>
                </div>
                {value === 0 && <TabContainer>Home</TabContainer>}
                {value === 1 && <TabContainer>About</TabContainer>}
                {value === 2 && <TabContainer>Work</TabContainer>}
            </div>
        );
    }
}

export default Header;
